const debug = require('debug');

module.exports = function(name){
    const log = debug(`${name}:log`);
    log.log = console.log.bind(console); // don't forget to bind to console!
    const error = debug(`${name}:error`);
    error.error = console.error.bind(console); // don't forget to bind to console!
    const info = debug(`${name}:info`);
    info.log = console.info.bind(console); // don't forget to bind to console!

    return {log, error, info}
};
