const Promise = require('bluebird');
const Sqlite = require('sqlite-pool');
const config = require('../../conf');
const path = require('path');
const {log, error} = require('debugger')("utils");

function dbPath() {
    let result;
    switch (config.get('db-env')) {
        case "dev":
            result = path.resolve(__dirname, '../..', "db/dev.sqlite");
	    log(`result '${result}'`)
            break;
        case "prod":
            result = path.resolve(__dirname, '../..', "db/prod.sqlite");
	    log(`result '${result}'`)
            break;
        default:   
            result = "";
	    log(`result '${result}'`)
    }
    log(`[getDbPath][spath] ${result}`);
    return result;
}

function getDB() {
    return new Sqlite(dbPath(), {Promise});
}

function getKnex(){
    const knex = require('knex')({
        client: 'sqlite3',
        connection: {
            filename: dbPath()
        }
    });
    return knex;
}

module.exports = {
    dbPath,
    getKnex,
    getDB
};
